def int_to_str(x_p=0):
    x = str(bin(x_p))[2:]
    x = ("0" * (8 - (len(x) % 8))) + x
    # print(len(x))
    ans = []
    while len(x) >= 8:
        ans = [""] + ans
        for i in range(8):
            ans[0] = x[-1] + ans[0]
            x = x[:-1]
    res = ""
    for i in range(len(ans)):
        ans[i] = [0, list(ans[i])]
        for i2 in range(8):
            if ans[i][1][-1] == "1":
                ans[i][0] = ans[i][0] + (2 ** (i2))
            ans[i][1] = ans[i][1][:-1]
    for i in range(len(ans)):
        res = res + dez_to_char(str(ans[i][0]))
    return res


def hexbyte_to_int(hex_p=""):
    x = list(hex_p)
    symbols = list("0123456789abcdef")
    ans = ""
    for i in range(len(x)):
        x[i] = x[i].lower()
        foo = str(bin(symbols.index(x[i])))[2:]
        ans = ans + ("0" * (4 - len(foo))) + foo
    ans = int(str(int(ans, 2)))
    return ans


def ctf4(x_p=0):
    x = hex(x_p)[2:]
    print(x)
    l = []
    while len(x) > 0:
        l.append(x[-2:])
        x = x[:-2]
    l.reverse()
    print(l)
    ans = ""
    for i in range(len(l)):
        ans = ans + chr(int(l[i], 16))
    return ans
